SELECT a.*,u1.chinese_name createUser,u.chinese_name storeManager,si.name storeName FROM ( SELECT fo.id oid, fo.out_quantity, fo.finished_id oFinishedId, fo.out_user, fo.principal, fo.create_id, fo.create_time, f.id, f.harvest_batch_id harvestBatchId, f.finished_name finishedName, f.finished_quantity finishedQuantity, f.finished_low finishedLow, f.finished_out finishedOut, f.dic_tea_attr dicTeaAttr, f.dic_tea_type dicTeaType, f.packaing_id, p.id pid, p.product_name productName, p.package_id packageId, p.tags_id tagsId, p.licence_id licenceId, p.standard_id standardId, p.descriptin descriptin, pg.spe_one speOneP, pg.spe_two speTwoP, pg.spe_there speThereP, pg.spe_four speFourP, pg.package_quantity packageQuantity, pg.package_use packageUse, d. name attrName, d1.name typeName FROM c_finished_out fo, c_finished f LEFT JOIN c_products p ON f.priduct_id = p.id LEFT JOIN s_dictionary d ON f.dic_tea_attr = d.id LEFT JOIN s_dictionary d1 ON f.dic_tea_type = d1.id LEFT JOIN c_package pg ON f.packaing_id = pg.id WHERE 1 = 1 AND fo.finished_id = f.id
AND fo.harvest_batch_id = #{harvestBatchId}
AND fo.dic_tea_attr = #{dicTeaAttr}
AND fo.dic_tea_attr = #{dicTeaType}
AND fo.id = #{id}
)a LEFT JOIN s_store_info si ON a.out_user = si.id LEFT JOIN s_users u ON a.principal = u.id LEFT JOIN s_users u1 ON a.create_id = u1.id

SELECT GROUP_CONCAT(d.name, f.out_quantity,concat("ä»¶")) allTea FROM c_finished_out f LEFT JOIN s_dictionary d ON f.dic_tea_attr = d.id WHERE 1 = 1
AND f.out_user = #{outUser}
AND date_format(f.create_time,'%y-%m-%d') = date_format(#{dateTime},'%y-%m-%d')
