mapper.select:
SELECT a.id, a.name, a.status,a.maintain_cycle maintainCycle, a.sort_id sortId,a.description,a.run_num runNum, e.unit_name unitName,e.id unitId,a.all_run_num allRunNum, e.unit_no unitNo,a.maintain_clear maintainClear, e.unit_usage unitUsage,a.dic_tea_attr, e.unit_type unitType, e.unit_cycle unitCycle, e.status euStatus, e.unit_status unitStatus, e.unit_clear unitClear, d.name attrName FROM e_assembly_set a LEFT JOIN e_equipment e ON a.machine_id = e.id LEFT JOIN s_dictionary d ON a.dic_tea_attr = d.id
WHERE a.id in( select id from ( select id from e_assembly_set where id = #{parentId} union select id from e_assembly_set where parent_id = #{parentId} ) a where 1 =1 )
WHERE 1 = 1
AND a.status = #{status}
AND e.unit_status = #{unitStatus}
AND e.status = #{euStatus}
AND a.dic_tea_attr = #{dicTeaAttr}
order by a.create_time desc

mapper.update:
update e_assembly_set
set
run_num = run_num+1,
all_run_num = all_run_num+1,
modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT
    e.*,d.name attrName,u.chinese_name createUser
FROM
    e_assembly_set a, e_equipment e
LEFT JOIN s_dictionary d ON e.unit_usage = d.id
LEFT JOIN s_users u ON e.create_id = u.id
where 1 = 1
AND a.machine_id = e.id

  AND a.parent_id = #{id}


  AND a.dic_tea_attr = #{dicTeaAttr}
  AND e.dic_tea_attr = #{dicTeaAttr}


  AND e.unit_usage = #{unitUsage}
