mapper.select:
select e.id,e.unit_name unitName,e.unit_no unitNo, e.unit_usage unitUsage,u.chinese_name createName, e.unit_type unitType, e.unit_cycle unitCycle,e.status,d.name equName from s_users u,e_equipment e LEFT JOIN s_dictionary d ON e.unit_usage = d.id where 1 = 1 AND e.create_id = u.id
AND e.status = #{status}
AND e.unit_status = #{unitStatus}
order by e.create_time desc

SELECT a.*,ms.name FROM( SELECT e.id eqId, s.parent_id,e.unit_name,e.unit_no,e.unit_type,e.img_url,e.status unitStatus,d.name unitDicName,em.create_time,d1.name mainName FROM e_assembly_set s, e_equipment e LEFT JOIN s_dictionary d ON e.unit_usage = d.id LEFT JOIN e_equipment_maintain em ON e.id = em.equipment_id AND em.maintain_status = 2 LEFT JOIN s_dictionary d1 ON em.maintain_type = d1.id WHERE 1 = 1
AND s.parent_id = #{parentId}
AND s.machine_id = e.id
AND s.dic_tea_attr = #{dicTeaAttr}
AND s.machine_id = #{machineId}
)a LEFT JOIN e_assembly_set ms ON a.parent_id = ms.id GROUP BY a.eqId ORDER BY a.create_time DESC LIMIT 1
