mapper.select:
select

from
    s_dictionary d
where
  1 = 1

    and d.parent_id = #{parentId}


    and d.id = #{id}


    and d.id = #{value}


    and d.status = #{status}

order by
    d.sort_id asc;

mapper.delete:
delete
from s_dictionary
    where id in(
        select id from (
            select id from s_dictionary where id = #{id}
            union
            select id from s_dictionary where parent_id  = #{id}
        ) a where 1 =1
    )
